// Simplified classification for Google Drive files only (no Gmail)
const crypto = require('crypto');
const processedItems = [];

console.log('=== CONTENT CLASSIFICATION (Drive Only) ===');
console.log(`Input items: ${$input.all().length}`);

for (const item of $input.all()) {
  const data = item.json;

  // All items are Google Drive files now (no Gmail)
  if (!data.file_name || !data.file_id) {
    console.log(`Skipping item with missing file data`);
    continue;
  }

  const contentForHash = data.file_name + 
                         (data.modified_time || '') + 
                         (data.version || '') + 
                         data.team_id; // Include team_id in hash

  const contentHash = crypto.createHash('md5')
    .update(contentForHash)
    .digest('hex');

  const classification = {
    source_type: 'google_drive',
    source_id: data.file_id,
    title: data.file_name,
    content_hash: contentHash,
    modified_time: data.modified_time,
    should_process: true,
    is_recent: true,
    folder_type: data.folder_type, // Pass through folder type
    team_id: data.team_id // Pass through team_id
  };

  processedItems.push({
    ...data,
    classification: classification
  });

  console.log(`Classified: ${data.file_name} (${data.folder_type}, team: ${data.team_id})`);
}

console.log(`=== CLASSIFICATION RESULTS ===`);
console.log(`Processed ${processedItems.length} Google Drive files`);

return processedItems.map(item => ({ json: item }));

// Fast hash-based deduplication - no database queries needed
const crypto = require('crypto');
console.log('=== FAST HASH DEDUPLICATION ===');

const contentToProcess = $input.all();
console.log(`Input items: ${contentToProcess.length}`);

// Since we're using time filtering, we expect minimal duplicates
// Use content hash for edge case protection
const seenHashes = new Set();
const newContent = [];
const duplicates = [];

for (const item of contentToProcess) {
  const data = item.json;
  const classification = data.classification;

  if (!classification || !classification.should_process) {
    console.log(`Skipping: ${classification?.title || 'unknown'} - not marked for processing`);
    continue;
  }

  // Create enhanced hash including source info
  const enhancedHash = crypto.createHash('md5')
    .update(`${classification.source_type}:${classification.source_id}:${classification.modified_time}`)
    .digest('hex');

  if (seenHashes.has(enhancedHash)) {
    duplicates.push({
      title: classification.title,
      hash: enhancedHash.substring(0, 8)
    });
    console.log(`Duplicate detected: ${classification.title}`);
    continue;
  }

  seenHashes.add(enhancedHash);
  newContent.push({
    ...data,
    processing_hash: enhancedHash,
    processing_timestamp: new Date().toISOString()
  });

  console.log(`Added: ${classification.title} (${classification.source_type})`);
}

console.log(`=== DEDUPLICATION RESULTS ===`);
console.log(`New items: ${newContent.length}`);
console.log(`Duplicates found: ${duplicates.length}`);

if (newContent.length === 0) {
  console.log('No new content to process - workflow complete');
  return [];
}

return newContent.map(item => ({ json: item }));

