}

console.log(`Prepared ${results.length} documents for storage`);
return results.map(item => ({ json: item }));

// Optimized chunking with smart sentence boundaries - WITH team_id
const chunks = [];
const chunkSize = 1000;
const overlap = 200;

for (const item of $input.all()) {
  const doc = item.json;
  const content = doc.content;

  if (!content || content.length < 100) {
    console.log(`Skipping chunking for ${doc.title} - content too short`);
    continue;
  }

  // Smart chunking at sentence boundaries
  const sentences = content.split(/[.!?]+/).filter(s => s.trim().length > 0);
  let currentChunk = '';
  let chunkIndex = 0;

  for (const sentence of sentences) {
    const trimmedSentence = sentence.trim();
    if (!trimmedSentence) continue;

    // Check if adding this sentence would exceed chunk size
    if (currentChunk.length + trimmedSentence.length > chunkSize && currentChunk.length > 0) {
      // Save current chunk
      chunks.push({
        content: currentChunk.trim(),
        chunk_index: chunkIndex,
        source_id: doc.source_id,
        source_type: doc.source_type,
        title: doc.title,
        batch_id: doc.batch_id,
        team_id: doc.team_id, // ← NEW
        user_id: doc.user_id, // ← NEW
        folder_type: doc.folder_type, // ← NEW
        processing_timestamp: doc.processing_timestamp,
        metadata: {
          ...doc.metadata,
          team_id: doc.team_id, // ← NEW
          folder_type: doc.folder_type // ← NEW
        }
      });

      // Start new chunk with overlap
      const words = currentChunk.split(' ');
      const overlapWords = words.slice(-Math.floor(overlap / 5));
      currentChunk = overlapWords.join(' ') + ' ' + trimmedSentence;
      chunkIndex++;
    } else {
      currentChunk += (currentChunk ? '. ' : '') + trimmedSentence;
    }
  }

  // Add final chunk
  if (currentChunk.trim().length > 0) {
    chunks.push({
      content: currentChunk.trim(),
      chunk_index: chunkIndex,
      source_id: doc.source_id,
      source_type: doc.source_type,
      title: doc.title,
      batch_id: doc.batch_id,
      team_id: doc.team_id, // ← NEW
      user_id: doc.user_id, // ← NEW
      folder_type: doc.folder_type, // ← NEW
      processing_timestamp: doc.processing_timestamp,
      metadata: {
        ...doc.metadata,
        team_id: doc.team_id, // ← NEW
        folder_type: doc.folder_type // ← NEW
      }
    });
  }
}

console.log(`Created ${chunks.length} chunks for embedding`);

if (chunks.length === 0) {
  console.log('No chunks created - ending workflow');
  return [];
}

return chunks.map(chunk => ({ json: chunk }));

// Prepare vector chunks with team_id and folder_type
const vectorChunks = [];

function safeGetNodeData(nodeName) {
  try {
    return $(nodeName).all();
  } catch (error) {
    console.log(`Warning: Could not access node "${nodeName}": ${error.message}`);
    return [];
  }
}

const chunkDataArray = safeGetNodeData('Chunk Content');
const embeddingDataArray = safeGetNodeData('Generate OpenAI Embeddings1') || 
                           safeGetNodeData('Generate OpenAI Embeddings');

console.log(`Available chunks: ${chunkDataArray.length}`);
console.log(`Available embeddings: ${embeddingDataArray.length}`);

const maxItems = Math.min(chunkDataArray.length, embeddingDataArray.length);

if (maxItems === 0) {
  console.log('No matching chunk/embedding data found');
  return [];
}

for (let i = 0; i < maxItems; i++) {
  const chunkData = chunkDataArray[i]?.json;
  const embeddingData = embeddingDataArray[i]?.json;

  if (!chunkData || !embeddingData?.data?.[0]?.embedding) {
    console.log(`Skipping chunk ${i} - missing data or embedding`);
    continue;
  }

  // Get document type from folder_type
  let documentType = chunkData.folder_type === 'meetings' ? 'Meetings' : 'Strategy';

  // Extract title and date
  let documentTitle = chunkData.title || chunkData.metadata?.file_name || 'Unknown Document';
  let documentDate = chunkData.metadata?.modified_time || new Date().toISOString();

  // Clean and truncate title
  documentTitle = documentTitle.trim().substring(0, 500);

  // Ensure documentDate is a valid ISO string
  try {
    if (documentDate && !documentDate.endsWith('Z') && !documentDate.includes('+')) {
      documentDate = new Date(documentDate).toISOString();
    }
  } catch (error) {
    console.log(`Warning: Invalid date format, using current time: ${error.message}`);
    documentDate = new Date().toISOString();
  }

  // Extract batch_id and team_id
  const batch_id = chunkData.batch_id || chunkData.metadata?.batch_id;
  const team_id = chunkData.team_id || chunkData.metadata?.team_id;

  if (!batch_id) {
    console.log(`WARNING: Chunk ${i} missing batch_id!`);
  }

  if (!team_id) {
    console.log(`WARNING: Chunk ${i} missing team_id!`);
  }

  // Create metadata for storage
  const metadataForDB = {
    document_name: documentTitle,
    chunk_index: chunkData.chunk_index,
    document_type: documentType,
    source_type: chunkData.source_type,
    source_id: chunkData.source_id,
    processing_timestamp: chunkData.processing_timestamp,
    document_date: documentDate,
    batch_id: batch_id,
    team_id: team_id, // ← NEW
    folder_type: chunkData.folder_type, // ← NEW
    ...chunkData.metadata
  };

  // Format embedding as PostgreSQL vector
  const embedding = embeddingData.data[0].embedding;

  if (!Array.isArray(embedding) || embedding.length === 0) {
    console.log(`Skipping chunk ${i} - invalid embedding format`);
    continue;
  }

  const vectorString = `[${embedding.join(',')}]`;

  console.log(`Preparing chunk ${i}: "${documentTitle}" (${chunkData.folder_type}, team: ${team_id})`);

  vectorChunks.push({
    content: chunkData.content,
    title: documentTitle,
    document_date: documentDate,
    batch_id: batch_id,
    team_id: team_id, // ← NEW
    folder_type: chunkData.folder_type, // ← NEW for routing
    metadata: JSON.stringify(metadataForDB),
    embedding: vectorString
  });
}

console.log(`Prepared ${vectorChunks.length} vector chunks with team_id and folder_type`);

if (vectorChunks.length === 0) {
  console.log('No vector chunks to store');
  return [];
}

return vectorChunks.map(chunk => ({ json: chunk }));

