// Preserve Google Drive metadata after code-based download
const processedGoogleDocs = [];

// The Download Document Content node already combined everything!
// We just need to pass it through with the right structure
const downloadedItems = $input.all();

console.log(`Processing ${downloadedItems.length} downloaded items`);

for (const item of downloadedItems) {
  const data = item.json;

  // Check if we have content
  if (!data.content || data.content.length < 10) {
    console.log(`Warning: No content for file: ${data.file_name}`);
    continue;
  }

  // The Download node should have preserved the original metadata
  // We need to get it from the Enhanced Content Classification node
  const originalFiles = $('Enhanced Content Classification').all();
  const matchingFile = originalFiles.find(f => 
    (f.json.file_id || f.json.id) === data.file_id
  );

  if (!matchingFile) {
    console.log(`Warning: Could not find original metadata for ${data.file_name}`);
    continue;
  }

  // Combine downloaded content with ALL original metadata
  const combinedData = {
    ...matchingFile.json, // ALL original metadata including team_id, folder_type, etc.
    data: data.content, // The downloaded text content
    content_length: data.content.length,
    download_timestamp: new Date().toISOString(),
    processing_step: 'content_downloaded'
  };

  processedGoogleDocs.push(combinedData);

  console.log(`Combined: ${combinedData.file_name} (${data.content.length} chars, team: ${combinedData.team_id}, type: ${combinedData.folder_type})`);
}

console.log(`Successfully processed ${processedGoogleDocs.length} Google Drive documents with preserved metadata`);

return processedGoogleDocs.map(doc => ({ json: doc }));

