// Filter out files that already exist and haven't been modified
const inputItems = $input.all(); // All files from Add Team Metadata

console.log(`=== FILTER NEW/UPDATED FILES ===`);
console.log(`Input files: ${inputItems.length}`);

// Get existing files by directly accessing the Check Existing nodes
let existingFilesMap = new Map();

try {
  // Access Check Existing - Meetings results
  const meetingsResults = $('Check Existing - Meetings').all();
  console.log(`\nCheck Existing - Meetings returned ${meetingsResults.length} items`);

  if (meetingsResults.length > 0) {
    const firstResult = meetingsResults[0];
    console.log(`First result keys:`, Object.keys(firstResult));
    console.log(`First result.json type:`, typeof firstResult.json);
    console.log(`First result.json keys:`, Object.keys(firstResult.json || {}));
    console.log(`First result.json content:`, JSON.stringify(firstResult.json).substring(0, 500));
  }

  for (let i = 0; i < meetingsResults.length; i++) {
    const result = meetingsResults[i];

    // The HTTP Request node returns the response body in result.json
    // But we need to check if it's an array directly, or if the array is nested
    let dbRecords = result.json;

    // If result.json is not an array, it might be an object containing the array
    // Check common patterns
    if (!Array.isArray(dbRecords)) {
      // Try common response wrappers
      if (dbRecords && Array.isArray(dbRecords.data)) {
        dbRecords = dbRecords.data;
      } else if (dbRecords && Array.isArray(dbRecords.rows)) {
        dbRecords = dbRecords.rows;
      } else if (dbRecords && Array.isArray(dbRecords.results)) {
        dbRecords = dbRecords.results;
      } else {
        console.log(`  Meetings ${i}: Unexpected structure, treating as empty`);
        continue;
      }
    }

    if (dbRecords.length > 0) {
      console.log(`  Meetings ${i}: Found ${dbRecords.length} records`);
      for (const record of dbRecords) {
        const sourceId = record.metadata?.source_id;
        const docDate = record.document_date;
        if (sourceId && docDate) {
          existingFilesMap.set(sourceId, docDate);
        }
      }
    }
  }

  // Access Check Existing - Strategy results
  const strategyResults = $('Check Existing - Strategy').all();
  console.log(`\nCheck Existing - Strategy returned ${strategyResults.length} items`);

  for (let i = 0; i < strategyResults.length; i++) {
    const result = strategyResults[i];
    let dbRecords = result.json;

    if (!Array.isArray(dbRecords)) {
      if (dbRecords && Array.isArray(dbRecords.data)) {
        dbRecords = dbRecords.data;
      } else if (dbRecords && Array.isArray(dbRecords.rows)) {
        dbRecords = dbRecords.rows;
      } else if (dbRecords && Array.isArray(dbRecords.results)) {
        dbRecords = dbRecords.results;
      } else {
        continue;
      }
    }

    if (dbRecords.length > 0) {
      console.log(`  Strategy ${i}: Found ${dbRecords.length} records`);
      for (const record of dbRecords) {
        const sourceId = record.metadata?.source_id;
        const docDate = record.document_date;
        if (sourceId && docDate) {
          existingFilesMap.set(sourceId, docDate);
        }
      }
    }
  }

} catch (error) {
  console.log(`ERROR: ${error.message}`);
}

console.log(`\nTotal existing files in map: ${existingFilesMap.size}`);

// Filter - only keep new or updated files
const newOrUpdatedFiles = [];

for (const item of inputItems) {
  const fileId = item.json.file_id;
  const fileName = item.json.file_name;
  const modifiedTime = item.json.modified_time;

  if (!fileId || !fileName) {
    continue;
  }

  if (!existingFilesMap.has(fileId)) {
    console.log(`✓ NEW: ${fileName}`);
    newOrUpdatedFiles.push(item);
  } else {
    const existingDate = new Date(existingFilesMap.get(fileId));
    const fileDate = new Date(modifiedTime);

    if (fileDate > existingDate) {
      console.log(`✓ UPDATED: ${fileName}`);
      newOrUpdatedFiles.push(item);
    } else {
      console.log(`✗ SKIP: ${fileName} (current)`);
    }
  }
}

console.log(`\n=== RESULTS ===`);
console.log(`Input: ${inputItems.length}, Existing: ${existingFilesMap.size}, To process: ${newOrUpdatedFiles.length}`);

return newOrUpdatedFiles;

const items = [];

for (const item of $input.all()) {
  const fileId = item.json.file_id || item.json.id;
  const accessToken = item.json.access_token;
  const fileName = item.json.name;

  try {
    const response = await this.helpers.httpRequest({
      method: 'GET',
      url: `https://www.googleapis.com/drive/v3/files/${fileId}/export?mimeType=text/plain`,
      headers: {
        'Authorization': `Bearer ${accessToken}`
      }
    });

    items.push({
      json: {
        file_id: fileId,
        file_name: fileName,
