  connectionData = {
    id: inputData.connection_id || 'unknown',
    team_id: inputData.team_id || 'unknown',
    user_id: inputData.user_id || 'unknown'
  };
}

console.log(`Sync complete for connection ${connectionData.id}`);
console.log(`Stored ${storedCount} chunks`);
console.log(`Team: ${connectionData.team_id}`);

return [{
